import React from 'react';
import './App.css';
import { WebMapView } from './components/WebMapView';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h1>Solar Calculator</h1>
      </header>

      <WebMapView />
      <div id="mapDiv"></div>
    <div id="info" class="esriSimpleSlider">
      Draw a polygon where you would like your solar installation to calculate the nominal power of the installation.
      <br /><br />
      <span class="label">Area:</span> <span id="area"></span>
      <br />
      <span class="label">Length:</span> <span id="length"></span>
    </div>
    </div>
  );
}

export default App;
