// Adapted from "Using the ArcGIS API for JavaScript with React"
// found here: https://developers.arcgis.com/javascript/latest/guide/react/

import React from 'react';
import { loadModules } from 'esri-loader';
// import geometryService from '../scripts/geometryService'

export class WebMapView extends React.Component {
  constructor(props) {
    super(props);
    this.mapRef = React.createRef();
    this.state = {stylePath: 'https://js.arcgis.com/3.31/esri/css/esri.css'};
  }

  componentDidMount() {

    // lazy load the required ArcGIS API for JavaScript modules and CSS
    loadModules(['esri/Map', 'esri/views/MapView', 'esri/widgets/Search', 'dojo/_base/lang', 'esri/toolbars/draw' ], { css: true })
    .then(([ArcGISMap, MapView, Search, lang, Draw]) => {
      const map = new ArcGISMap({
        basemap: 'topo-vector'
      });

      let view = new MapView({
        container: this.mapRef.current,
        map: map,
        center: [-118, 34],
        zoom: 8
      });

      // add searchbar 
      let search = new Search({
        view: view
      });
      view.ui.add(search, "top-right");

      let draw = new Draw(map);
      draw.on("draw-end", lang.hitch(map, getAreaAndLength));
      draw.activate(Draw.FREEHAND_POLYGON);

      function getAreaAndLength(evtObj) {
        var map = this,
            geometry = evtObj.geometry;
        map.graphics.clear();

        var graphic = map.graphics.add(new Graphic(geometry, new SimpleFillSymbol()));

        //setup the parameters for the areas and lengths operation
        var areasAndLengthParams = new AreasAndLengthsParameters();
        areasAndLengthParams.lengthUnit = GeometryService.UNIT_FOOT;
        areasAndLengthParams.areaUnit = GeometryService.UNIT_ACRES;
        areasAndLengthParams.calculationType = "geodesic";
        geometryService.simplify([geometry], function(simplifiedGeometries) {
          areasAndLengthParams.polygons = simplifiedGeometries;
          geometryService.areasAndLengths(areasAndLengthParams);
        });
      }

    });
  }


  componentWillUnmount() {
    if (this.view) {
      // destroy the map view
      this.view.container = null;
    }
  }

  render() {
    return (
      <div className="webmap" ref={this.mapRef} />
    )
  }
}
